namespace Core\DeliveryBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * AddressRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AddressRepository extends EntityRepository
{
    /**
     * Поиск с городом
     * @param boolean $isEnabled - вкл или выкл
     * @return type
     */
    public function findWithCity($isEnabled = true)
    {
        $qb = $this->createQueryBuilder('a');

        $qb->select('a, c')
            ->leftJoin('a.city', 'c')
            ->where('a.status = :isEnabled')
        ;

        $qb->setParameter('isEnabled', $isEnabled);

        $result = $qb->getQuery()->getResult();

        return $result;
    }

    /**
     *
     * @param integer $id
     * @param boolean $isEnabled
     * @return type
     */
    public function findOneWithCity($id, $isEnabled = true)
    {
        $qb = $this->createQueryBuilder('a');

        $qb->select('a, c, r')
            ->leftJoin('a.city', 'c')
            ->leftJoin('c.region', 'r')
            ->where('a.status = :isEnabled')
            ->andWhere('a.id = :id')
        ;

        $qb->setParameter('isEnabled', $isEnabled)
           ->setParameter('id', $id);

        $result = $qb->getQuery()->getOneOrNullResult();

        return $result;
    }
}
