namespace Core\DeliveryBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * ServiceWithAddressRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ServiceWithAddressRepository extends EntityRepository
{
    /**
     * Подсчет кол-ва точек самовывоза для города
     * @param int $cityId - id города
     */
    public function findSelfPickUp($cityId)
    {
        $qb = $this->createQueryBuilder('s');

        $qb->select('COUNT(s.id) as total')
            ->innerJoin('s.addresses', 'a')
            ->innerJoin('a.city', 'c')
            ->where('c.id = :cityId')
        ;

        $qb->setParameter('cityId', $cityId);

        $result = $qb->getQuery()->getSingleScalarResult();

        return $result;
    }
}
